sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue == '') {
        return;
    }

    // Do not continue if form is loading and is not a new record
    if (isLoading && !g_form.isNewRecord()) {
        return;
    }
    var whatNeeded = g_form.getValue('u_request_type');
    // Do not continue, if the action requested is any of the below
    if (newValue == ('journal entry only' || 'no response required' || 'response requested - no due date')) {
		// reset when needed if request type changed to type not needing a due date
        g_form.clearValue('u_when_needed');
        jslog('When Needed is not necessary for: ' + whatNeeded);
        return;
    }

    // Define default value of the When needed field based on the Request type
    var defaultdueDays;

    if (newValue == 'response requested - with due date') {
        defaultdueDays = 1;
    }

    if (newValue == 'dir_signature') {
        defaultdueDays = 2;
    }

    if (newValue == 'gov referral request') {
        defaultdueDays = 3;
    }

    if (newValue == 'letter approval request') {
        defaultdueDays = 4;
    }
    jslog('When Needed needed for: ' + whatNeeded);
    jslog("The number of days from today used for due date calculation is: " + defaultdueDays);

    var nowGdt = new GlideDateTime();
    //var nowGdt = new GlideDate();	  <-- doesn't work
    //var nowGdt = new GlideDateTime().GetDisplayValue();  <-- doesn't work
    var dueDays = defaultdueDays;
    var myScheduleName = '8-5 weekdays';
    var dueWorkingHours = 8;
    var dueSeconds = dueDays * dueWorkingHours * 60 * 60;
    var leadTime = new GlideDuration(dueSeconds * 1000);
    var dueDateGdt;
    var schedRec = new GlideRecord('cmn_schedule');

    if (schedRec.get('name', myScheduleName)) {
        var sched = new GlideSchedule(schedRec.sys_id);
        dueDateGdt = sched.add(nowGdt, leadTime, '');
    }

    g_form.clearValue('u_when_needed');
    g_form.setValue('u_when_needed', dueDateGdt);

}
