sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    if (newValue == 'journal entry only') {
        g_form.clearValue('u_when_needed');
        //g_form.clearValue('assigned_to');
        //g_form.clearValue('group_list');
        //g_form.clearValue('additional_assignee_list');
        g_form.clearValue('u_responsible_group');
        g_form.clearValue('u_route_to_group_list');
        g_form.clearValue('opened_at');
        g_form.setMandatory('opened_at', false); //opened_at field becomes non-mandatory.  Need this or will not save. 

    } else if (newValue == 'no response required') {
        g_form.clearValue('u_when_needed');

    } else if (oldValue == 'dir review') {
		g_form.clearValue('u_responsible_group');
	}

}
