sys_script_client.script:
function onSubmit() {
    if (g_form.isNewRecord()) {
        return;
    } else {
        var whatState = g_form.getValue('state');
        if (whatState == '-5' || whatState == '30' || whatState == '3' || whatState == '55') {
            g_form.setReadOnly('u_request_type', true);
        }
    }
}
