sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    g_form.setReadonly('number', true);
    g_form.setReadonly('u_log_date', true);
    g_form.setReadonly('active', true);
    g_form.setReadonly('u_division', true);
    g_form.setReadonly('u_office_section', true);

    // Hide the Journal Information for new documents
    if (g_form.isNewRecord()) {
        g_form.setSectionDisplay('journal_information', false);
    }

	// Display the Route Slip Information Section only for certain roles
    if (g_user.hasRoleExactly('x_sohd3_dotmail.DIR Contributors') || g_user.hasRoleExactly('admin')) {
        g_form.setSectionDisplay('route_slipinformation', true);
    } else {
        g_form.setSectionDisplay('route_slipinformation', false);
    }

    //alert(g_form.getSectionNames());
}
