sys_script_include.script:
var CalculateFutureDate = Class.create();
// Extend the global.AbstractAjaxProcessor class
CalculateFutureDate.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
    // Define the calculateDate function.  
    calculateDate: function() {
		//gs.info('In calculateDate function');
		var wdays = this.getParameter('sysparm_wdays');   // Get passed-in parameter number of days
		//gs.info('The passed-in parameter is: ' + wdays);
		var nowGdt = new GlideDateTime();
        var dueDays = wdays;
        var myScheduleName = '8-5 weekdays';
        var dueWorkingHours = 8;
        var dueSeconds = dueDays * dueWorkingHours * 60 * 60;
        var leadTime = new GlideDuration(dueSeconds * 1000);
        var dueDateGdt;
        var schedRec = new GlideRecord('cmn_schedule');

        if (schedRec.get('name', myScheduleName)) {
            var sched = new GlideSchedule(schedRec.sys_id);
            dueDateGdt = sched.add(nowGdt, leadTime, '');
        }
		//gs.info('Returning the following value: ' + dueDateGdt);
        return dueDateGdt;     // Return the due date for the requested record
    },
    type: 'CalculateFutureDate'
});
